const rows = Array.isArray(msg.payload) ? msg.payload : [];
const availableCount = rows.filter(b => (b?.status ?? "").toLowerCase() !== "in_use").length;

// Formate en anglais avec pluriel
msg.payload = `There ${availableCount === 1 ? "is" : "are"} ${availableCount} available bike${availableCount === 1 ? "" : "s"}`;
msg.topic = "hepl/disponibilities";

// Save last data
msg.retain = true;
return msg;

var station_id = flow.get("selected_station_id");
if (station_id) {
    msg.url = "https://hepl.local/smartpedals/api/stations/" + station_id;
} else {
    return null;
}

return msg;

const racks = Array.isArray(msg.payload) ? msg.payload : [];

// Per station
const byStation = {};
for (const r of racks) {
  const st = r?.station_id ?? "unknown";
  if (!byStation[st]) byStation[st] = { station_id: st, total: 0, occupied: 0 };
  byStation[st].total += 1;
  if (r?.currentBike) byStation[st].occupied += 1; // occupied if there is a bike
}

// Timestamp
const ts = new Date(); // timestamp ISO
const tz = "Europe/Brussels";
// (ex: "17/08/2025, 16:45:05")
const ts_brussels = ts.toLocaleString("fr-BE", { timeZone: tz });

const docs = Object.values(byStation).map(s => ({
  ts,
  ts_brussels,
  station_id: s.station_id,
  total_racks: s.total,
  occupied: s.occupied,
  available: s.total - s.occupied
}));

// if db is empty (after a cleanup for example) -> bypass the rest
if (msg.dbEmpty === true) {
  const baseline = {}; // init for the next diff
  for (const d of docs) {
    const k = String(d.station_id ?? "unknown");
    baseline[k] = {
      available: d.available,
      occupied: d.occupied,
      total_racks: d.total_racks
    };
  }
  flow.set("last_state_by_station", baseline);

  msg.payload = { documents: docs };  // send full info
  return msg;
}

// Check changes per station
// Keep last state in flow context
const prev = flow.get("last_state_by_station") || {}; // { [station_id]: {available, occupied, total_racks} }
const next = { ...prev };

const changed = [];
for (const d of docs) {
  const k = String(d.station_id ?? "unknown");
  const last = prev[k];

  // No change if available & occupied are identical.
  const same =
    last &&
    last.available === d.available &&
    last.occupied === d.occupied;

  if (!same) {
    changed.push(d);
    next[k] = {
      available: d.available,
      occupied: d.occupied,
      total_racks: d.total_racks
    };
  }
}

// Save latest state
flow.set("last_state_by_station", next);

// If nothing changed, stop
if (changed.length === 0) {
  return null;
}

// Otherwise, only send the changed stations
//msg.payload = { documents: docs };
msg.payload = { documents: changed };
return msg;

const PALETTE = [
  "#e6194B", "#3cb44b", "#ffe119", "#4363d8", "#f58231",
  "#911eb4", "#42d4f4", "#f032e6", "#bfef45", "#fabed4",
  "#469990", "#dcbeff", "#9A6324", "#fffac8", "#800000",
  "#aaffc3", "#808000", "#ffd8b1", "#000075", "#a9a9a9"
];

function getColorFromString(str) {
  // FNV-1a
  let h = 2166136261;
  for (let i = 0; i < str.length; i++) {
    h ^= str.charCodeAt(i);
    h = Math.imul(h, 16777619);
  }
  const idx = (h >>> 0) % PALETTE.length;
  return PALETTE[idx];
}

// Main function to process the bike locations
function processBikeLocations(msg) {
  const rows = Array.isArray(msg.payload) ? msg.payload : [];
  const perBike = new Map();

  // Group locations by bikeId
  rows.forEach((location) => {
    if (!location || !location.bike_id || !location.coordinates) return;

    const lat = parseFloat(location.coordinates.lat);
    const lon = parseFloat(location.coordinates.lon);
    if (isNaN(lat) || isNaN(lon)) return;

    const ts = new Date(location.timestamp);
    if (isNaN(ts.getTime())) return;

    if (!perBike.has(location.bike_id)) {
      perBike.set(location.bike_id, []);
    }
    perBike.get(location.bike_id).push({ bike_id: location.bike_id, lat, lon, ts });
  });

  // Process each bike's locations
  perBike.forEach((locations, bike_id) => {
    const sortedLocations = locations
      .sort((a, b) => a.ts - b.ts)
      .slice(-10); // Get the last 10 locations

    if (sortedLocations.length === 0) return;

    const color = getColorFromString(bike_id);

    // Send the polyline for the bike's track
    const line = sortedLocations.map((p) => [p.lat, p.lon]);
    node.send({
      payload: {
        name: `${bike_id}-track`,
        line,
        color,
        weight: 3,
        opacity: 0.85,
        layer: "bikes",
      },
    });

    // Send trail dots for all but the latest point
    for (let i = 0; i < sortedLocations.length - 1; i++) {
      const p = sortedLocations[i];
      const fillOpacity = 0.2 + (i / Math.max(1, sortedLocations.length - 2)) * 0.5;
      node.send({
        payload: {
          name: `${bike_id}#trail${i}`,
          lat: p.lat,
          lon: p.lon,
          radius: 7,
          color,
          fillColor: color,
          opacity: fillOpacity,
          fillOpacity,
          layer: "bikes",
        },
      });
    }

    // Send the latest point as a bicycle icon
    const last = sortedLocations[sortedLocations.length - 1];
    node.send({
      payload: {
        name: `${bike_id}#latest`,
        lat: last.lat,
        lon: last.lon,
        icon: "bicycle",
        iconColor: color,
        color,
        layer: "bikes",
      },
    });
  });

  // Fit the map to the bounds of the "bikes" layer
  node.send({ payload: { command: "fit", layer: "bikes" } });
  return null;
}

// Call the function with the msg object provided by Node-RED
processBikeLocations(msg);
